import { useEffect, useRef, useState } from 'react';
import square from './square.png';
import circle from './circle.png';

function Drawing() {
    const [drawing, setDrawing] = useState(false);
    const [minX, setMinX] = useState(1000);
    const [minY, setMinY] = useState(1000);
    const [maxX, setMaxX] = useState(-1);
    const [maxY, setMaxY] = useState(-1);
    const [canvasDone, setCanvasDone] = useState(false);
    const [curImg, setCurImg] = useState("");
    const [objs, setObjs] = useState({});
    const canvasRef = useRef(null);
    const ctxRef = useRef(null);

    const addObj = () => {
        console.log("begin");
        let new_objs = objs;
        let counter = Object.keys(new_objs).length;
        new_objs[counter.toString()] = {
            "name": counter.toString(),
            "x": 480,
            "y": 320,
        }
        console.log(new_objs);
        setObjs(new_objs);
    }
    const drawLine = (x1, y1, x2, y2, color) => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.beginPath();
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.strokeStyle = color;
        ctx.lineWidth = 2;
        ctx.stroke();
        ctxRef.current = ctx;
    }
    const confirmCanvas = () => {
        setCanvasDone(true);
        clear();
        drawLine(minX, minY, minX, maxY, "black");
        drawLine(minX, maxY, maxX, maxY, "black");
        drawLine(maxX, maxY, maxX, minY, "black");
        drawLine(maxX, minY, minX, minY, "black");
        console.log("done", maxX - minX, maxY - minY);
    }
    const updateCanvas = (x, y) => {
        if (x < minX) setMinX(x);
        if (y < minY) setMinY(y);
        if (x > maxX) setMaxX(x);
        if (y > maxY) setMaxY(y);
    }
    const startDraw = ({ nativeEvent }) => {
        if (canvasDone) return;
        const { offsetX, offsetY } = nativeEvent;
        ctxRef.current.beginPath();
        ctxRef.current.moveTo(offsetX, offsetY);
        setDrawing(true);
        if (!canvasDone) updateCanvas(offsetX, offsetY);
    };
    const stopDraw = () => {
        if (canvasDone) return;
        ctxRef.current.closePath();
        setDrawing(false);
    };
    const draw = ({ nativeEvent }) => {
        if (canvasDone) return;
        if (!drawing) return;
        const { offsetX, offsetY } = nativeEvent;
        ctxRef.current.lineTo(offsetX, offsetY);
        ctxRef.current.stroke();
        if (!canvasDone) updateCanvas(offsetX, offsetY);
    };
    const clear = () => {
        ctxRef.current.clearRect(
        0,
        0,
        canvasRef.current.width,
        canvasRef.current.height
        );
    };
    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.lineCap = 'round';
        ctx.strokeStyle = 'blue';
        ctx.lineWidth = 2;
        ctxRef.current = ctx;
    }, []);
    useEffect(() => {
        getObjs();
    }, []);
    return (
        <>
            <div>
                <canvas
                    onMouseDown={startDraw}
                    onMouseUp={stopDraw}
                    onMouseMove={draw}
                    ref={canvasRef}
                    width={960} 
                    height={640}
                />
                {trips && trips.map((trip, index) => (
                <Marker
                    key={`marker-${index}`}
                    longitude={trip.longitude}
                    latitude={trip.latitude}
                    anchor="bottom"
                    onClick={e => {
                    // If we let the click event propagates to the map, it will immediately close the popup
                    // with `closeOnClick: true`
                    e.originalEvent.stopPropagation();
                    setPopupInfo(trip);
                    }}
                >
                    <Pin city={trip}/>
                </Marker>
                ))}
            </div>

            <button onClick={confirmCanvas}>Done</button>
            <img src={square} width={50}/>
            <img src={circle} width={40}/>
            <button onClick={addObj}>Add</button>
        </>
    );
}

export default Drawing;
