import { useEffect, useRef, useState } from 'react';
import square from './assets/square.png';
import circle from './assets/circle.png';
import axios from "axios";
import Draggable, {DraggableCore} from "react-draggable";

function Drawing() {
    const [drawing, setDrawing] = useState(false);
    const [minX, setMinX] = useState(1000);
    const [minY, setMinY] = useState(1000);
    const [maxX, setMaxX] = useState(-1);
    const [maxY, setMaxY] = useState(-1);
    const [canvasDone, setCanvasDone] = useState(false);
    const [activeImg, setActiveImg] = useState("");
    const [objs, setObjs] = useState([]);
    const canvasRef = useRef(null);
    const ctxRef = useRef(null);
    const nodeRef = useRef(null);

    const addObj = () => {
        console.log("set");
        axios({
            method: "POST",
            url:"/data/add",
            params:{
                "type": "square",
                "x": 480,
                "y": 320,
            }
        })
        .then((response) => {
            console.log(response);
        });
    }
    const getObjs = () => {
        axios({
            method: "GET",
            url:"/data",
        })
        .then((response) => {
            setObjs(response.data);
        });
    }
    const updateObj = (id, x, y) => {
        const form = new FormData();
        form.append('items', '[{"id": "' + id + '", "x":' + x + ', "y":' + y + '}]'); 

        axios({
            method: "POST",
            url:"/data/update",
            data: form
        })
        .then((response) => {
            document.getElementsByClassName("react-draggble")
            setObjs(response.data);
        });
    }
    const xToRatio = (x) => {
        return (x - minX) / (maxX - minX)
    }
    const yToRatio = (y) => {
        return (y - minY) / (maxY - minY)
    }
    const ratioToX = (x) => {
        return x * (maxX - minX) + minX
    }
    const ratioToY = (y) => {
        return y * (maxY - minY) + minY
    }
    const drawLine = (x1, y1, x2, y2, color) => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.beginPath();
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.strokeStyle = color;
        ctx.lineWidth = 2;
        ctx.stroke();
        ctxRef.current = ctx;
    }
    const confirmCanvas = () => {
        setCanvasDone(true);
        clear();
        drawLine(minX, minY, minX, maxY, "black");
        drawLine(minX, maxY, maxX, maxY, "black");
        drawLine(maxX, maxY, maxX, minY, "black");
        drawLine(maxX, minY, minX, minY, "black");
        console.log("done", maxX - minX, maxY - minY);
    }
    const updateCanvas = (x, y) => {
        if (x < minX) setMinX(x);
        if (y < minY) setMinY(y);
        if (x > maxX) setMaxX(x);
        if (y > maxY) setMaxY(y);
    }
    const startDraw = ({ nativeEvent }) => {
        if (canvasDone) return;
        const { offsetX, offsetY } = nativeEvent;
        ctxRef.current.beginPath();
        ctxRef.current.moveTo(offsetX, offsetY);
        setDrawing(true);
        if (!canvasDone) updateCanvas(offsetX, offsetY);
    };
    const stopDraw = () => {
        if (canvasDone) return;
        ctxRef.current.closePath();
        setDrawing(false);
    };
    const draw = ({ nativeEvent }) => {
        if (canvasDone) return;
        if (!drawing) return;
        const { offsetX, offsetY } = nativeEvent;
        ctxRef.current.lineTo(offsetX, offsetY);
        ctxRef.current.stroke();
        if (!canvasDone) updateCanvas(offsetX, offsetY);
    };
    const clear = () => {
        ctxRef.current.clearRect(
        0,
        0,
        canvasRef.current.width,
        canvasRef.current.height
        );
    };
    const onMouseDownImage = (event) => {
        // console.log(event);
        // setActiveImg(event.target.id);
        // console.log("start");
    }
    const onMouseUpImage = (event) => {
        console.log(event)
        //console.log(activeImg);
        console.log(event.target.id, xToRatio(event.clientX), yToRatio(event.clientY));
        updateObj(event.target.id, xToRatio(event.clientX), yToRatio(event.clientY));
        // setActiveImg("");
    }
    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.lineCap = 'round';
        ctx.strokeStyle = 'blue';
        ctx.lineWidth = 2;
        ctxRef.current = ctx;
    }, []);
    useEffect(() => {
        const interval = setInterval(() => {
            getObjs();
        }, 1000);
        return () => clearInterval(interval);
      }, []);
    return (
        <>
            <div style={{position:"relative", width:"960", height:"640"}}>
                <canvas
                    onMouseDown={startDraw}
                    onMouseUp={stopDraw}
                    onMouseMove={draw}
                    ref={canvasRef}
                    width={960} 
                    height={640}
                />
                {canvasDone && objs && objs.map((obj, index) => (
                        <Draggable
                            key={obj.id} 
                            // onStart={onMouseDownImage}
                            onStop={onMouseUpImage}
                            >
                            <img id={obj.id} src={square} width={50} draggable={false}
                                style={{left:ratioToX(obj.x), top:ratioToY(obj.y), position:"absolute"}}
                            />
                        </Draggable>
                ))}
            </div>
            <button onClick={confirmCanvas}>Done</button>
            {/* <img src={square} width={50}/>
            <img src={circle} width={40}/>
            <button onClick={addObj}>Add</button> */}
        </>
    );
}

export default Drawing;
